import React from 'react';
import { GameStatusEnum } from './game-domain';

const StartGamePresentation = ({ gameState, onStartGame, onResetGame }) => {
  const handleStartButtonClick = () => {
    onStartGame(gameState);
  };

  const handleRestartButtonClick = () => {
    onResetGame(gameState);
  };

  if (gameState.status === GameStatusEnum.INITIAL) {
    return (
      <div className="absolute inset-0 flex flex-col items-center justify-center bg-black bg-opacity-75 text-white">
        <h1 className="text-6xl font-bold mb-4">SPACE INVADERS</h1>
        <p className="text-2xl mb-8">Press Start</p>
        <button
          onClick={handleStartButtonClick}
          className="px-8 py-4 bg-green-600 hover:bg-green-700 text-white text-2xl font-bold rounded-lg shadow-lg transition duration-300 ease-in-out"
        >
          START GAME
        </button>
      </div>
    );
  }

  if (gameState.status === GameStatusEnum.GAME_OVER) {
    return (
      <div className="absolute inset-0 flex flex-col items-center justify-center bg-black bg-opacity-75 text-white">
        <h1 className="text-6xl font-bold text-red-600 mb-4">GAME OVER</h1>
        <p className="text-3xl mb-8">SCORE: {gameState.score}</p>
        <button
          onClick={handleRestartButtonClick}
          className="px-8 py-4 bg-blue-600 hover:bg-blue-700 text-white text-2xl font-bold rounded-lg shadow-lg transition duration-300 ease-in-out"
        >
          PLAY AGAIN
        </button>
      </div>
    );
  }

  if (gameState.status === GameStatusEnum.GAME_WON) {
    return (
      <div className="absolute inset-0 flex flex-col items-center justify-center bg-black bg-opacity-75 text-white">
        <h1 className="text-6xl font-bold text-green-500 mb-4">YOU WON!</h1>
        <p className="text-3xl mb-8">SCORE: {gameState.score}</p>
        <button
          onClick={handleRestartButtonClick}
          className="px-8 py-4 bg-blue-600 hover:bg-blue-700 text-white text-2xl font-bold rounded-lg shadow-lg transition duration-300 ease-in-out"
        >
          PLAY AGAIN
        </button>
      </div>
    );
  }

  // Hide all elements of this presentation component if game status is PLAYING, PAUSED, etc.
  return null;
};

export default StartGamePresentation;