import React, { useState, useCallback } from 'react';
import { ExpenseCategoryType } from "./domain";

// Helper function to get today's date in YYYY-MM-DD format
const getTodayDateISO = () => new Date().toISOString().slice(0, 10);

/**
 * ExpenseForm component for adding new expenses.
 * It provides input fields for amount, description, date, and category,
 * and triggers a callback upon successful submission.
 *
 * @param {object} props - The component props.
 * @param {function} props.onAddExpense - Callback triggered when a new expense is submitted.
 */
export default function ExpenseForm({ onAddExpense }) {
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState(getTodayDateISO);
  // Initialize category with the first available category or an empty string if none exist
  const [category, setCategory] = useState(Object.values(ExpenseCategoryType)[0] || '');
  const [errorMessage, setErrorMessage] = useState('');

  /**
   * Handles the form submission.
   * Gathers input values, validates them, and triggers the onAddExpense callback.
   */
  const submitForm = useCallback((event) => {
    event.preventDefault();
    setErrorMessage(''); // Clear any previous error messages

    // 1. Retrieve the current values from the input fields (already in state)

    // 2. Validate the collected values against constraints.
    let isValid = true;
    const errors = [];

    const parsedAmount = parseFloat(amount);
    if (isNaN(parsedAmount) || parsedAmount <= 0) {
      errors.push('Amount must be a positive number.');
      isValid = false;
    }

    if (description.trim() === '') {
      errors.push('Description cannot be empty.');
      isValid = false;
    }

    const dateObj = new Date(date);
    if (date.trim() === '' || dateObj.toString() === 'Invalid Date') {
      errors.push('Date must be a valid ISO-8601 date.');
      isValid = false;
    }

    if (!Object.values(ExpenseCategoryType).includes(category)) {
      errors.push('Please select a valid category.');
      isValid = false;
    }

    if (isValid) {
      // 3. IF validation passes:
      // 3.1. Pass the collected { amount, description, date, category } object to the onAddExpense callback.
      onAddExpense({
        amount: parsedAmount,
        description: description.trim(),
        date,
        category,
      });

      // 3.2. Clear the form input fields.
      setAmount('');
      setDescription('');
      setDate(getTodayDateISO()); // Reset to current date
      setCategory(Object.values(ExpenseCategoryType)[0] || ''); // Reset to default category
    } else {
      // 4. ELSE: Display an error message to the user indicating invalid input.
      setErrorMessage(errors.join(' '));
    }
  }, [amount, description, date, category, onAddExpense]); // Dependencies for useCallback

  return (
    <form onSubmit={submitForm} className="p-4 bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-gray-800">Add New Expense</h2>

      {errorMessage && (
        <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded">
          {errorMessage}
        </div>
      )}

      <div className="mb-4">
        <label htmlFor="amount" className="block text-gray-700 text-sm font-bold mb-2">
          Amount (â‚¬)
        </label>
        <input
          type="number"
          id="amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          placeholder="e.g., 50.00"
          min="0.01" // Constraint: positive numeric values
          step="0.01"
          required
        />
      </div>

      <div className="mb-4">
        <label htmlFor="description" className="block text-gray-700 text-sm font-bold mb-2">
          Description
        </label>
        <input
          type="text"
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          placeholder="e.g., Groceries"
          required // Constraint: not empty
        />
      </div>

      <div className="mb-4">
        <label htmlFor="date" className="block text-gray-700 text-sm font-bold mb-2">
          Date
        </label>
        <input
          type="date"
          id="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          required // Constraint: valid date
        />
      </div>

      <div className="mb-6">
        <label htmlFor="category" className="block text-gray-700 text-sm font-bold mb-2">
          Category
        </label>
        <select
          id="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="shadow border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          required // Constraint: selected value
        >
          {Object.values(ExpenseCategoryType).map((cat) => (
            <option key={cat} value={cat}>
              {cat.charAt(0).toUpperCase() + cat.slice(1).toLowerCase()}
            </option>
          ))}
        </select>
      </div>

      <div className="flex items-center justify-between">
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Add Expense
        </button>
      </div>
    </form>
  );
}