import React, { useCallback } from 'react';

/**
 * HtmlInputEditor is a multi-line text input area designed for editing HTML content.
 * It occupies the left column of its parent container, is vertically resizable,
 * and uses a monospaced font for a code editing experience.
 */
export default function HtmlInputEditor({ value, onHtmlChange }) {
  /**
   * Handles changes to the textarea content and notifies the parent component.
   * @param {React.ChangeEvent<HTMLTextAreaElement>} event The change event from the textarea.
   */
  const handleChange = useCallback((event) => {
    onHtmlChange(event.target.value);
  }, [onHtmlChange]);

  return (
    <textarea
      className="w-full h-full p-2 border border-gray-300 rounded-md font-mono text-sm resize-y focus:outline-none focus:ring-2 focus:ring-blue-500"
      value={value}
      onChange={handleChange}
      placeholder="Enter HTML content here..."
      aria-label="HTML content editor"
    />
  );
}