import React from 'react';

/**
 * HtmlRenderer component
 * Role: Presentation
 *
 * Displays arbitrary HTML content within a sandboxed iframe.
 * It occupies the right column of its parent container, has a white background,
 * and a border to visually separate its content.
 *
 * The component ensures that JavaScript embedded within the htmlContent is not executed
 * for security reasons by using the `sandbox` attribute on the iframe, explicitly
 * omitting `allow-scripts`.
 *
 * @param {object} props - The component props.
 * @param {string} props.htmlContent - The HTML string to be rendered.
 */
export default function HtmlRenderer({ htmlContent }) {
  // The iframe's srcDoc attribute is used to render the HTML content directly.
  // The sandbox attribute is configured to prevent script execution and other
  // potentially harmful actions, while still allowing basic browser features
  // for standard rendering behavior.
  // Explicitly omitting 'allow-scripts' from the sandbox attribute ensures
  // that JavaScript within the provided HTML content is not executed.
  return (
    <div className="flex-1 bg-white border border-gray-300 overflow-hidden">
      <iframe
        title="HTML Renderer"
        srcDoc={htmlContent || ''} // Ensure srcDoc is always a string
        className="w-full h-full border-none" // Full width/height, no default iframe border
        // Sandbox attributes to allow basic browser functionality but prevent script execution.
        // 'allow-scripts' is intentionally omitted.
        sandbox="allow-same-origin allow-popups allow-forms allow-modals allow-orientation-lock allow-pointer-lock allow-presentation allow-downloads"
        frameBorder="0" // Remove default iframe border for a cleaner look
      ></iframe>
    </div>
  );
}