import React, { useState, useCallback } from 'react';
import HtmlInputEditor from './html-input-editor';
import HtmlRenderer from './html-renderer';

export default function Main() {
  // Capability: initialize - Sets up the initial state of the WYSIWYG editor.
  // Flow: Sets the initial htmlContent state.
  // Acceptance Criteria: Upon initialization, both display an empty string.
  const [htmlContent, setHtmlContent] = useState('');

  // Constraint: MUST ensure that changes in the HtmlInputEditor are immediately reflected in the HtmlRenderer.
  // The onHtmlChange event from HtmlInputEditor should trigger an update to the Main component's internal htmlContent state.
  const handleHtmlChange = useCallback((newHtmlContent) => {
    setHtmlContent(newHtmlContent);
  }, []);

  return (
    <div className="flex w-full h-screen p-4 gap-4 bg-gray-100">
      {/* Left column for HtmlInputEditor */}
      <div className="flex-1 flex flex-col border border-gray-300 rounded-lg overflow-hidden">
        <HtmlInputEditor
          value={htmlContent}
          onHtmlChange={handleHtmlChange}
        />
      </div>

      {/* Right column for HtmlRenderer */}
      <div className="flex-1 flex flex-col border border-gray-300 rounded-lg overflow-hidden bg-white">
        <HtmlRenderer
          htmlContent={htmlContent}
        />
      </div>
    </div>
  );
}