export function beautifyJson(rawJson) {
  try {
    const parsedObject = JSON.parse(rawJson);
    // Constraint: The beautifyJson capability MUST use 2 spaces for indentation.
    const beautifiedJson = JSON.stringify(parsedObject, null, 2);
    return beautifiedJson;
  } catch (error) {
    // Acceptance Criteria: When beautifyJson receives invalid JSON, it returns a descriptive error message string.
    return `Invalid JSON: ${error.message}`;
  }
}

export async function copyToClipboard(text) {
  try {
    // Flow: Attempt to write text to the system clipboard.
    // Constraints: The copyToClipboard capability MUST handle potential browser security restrictions
    // or user permissions gracefully, without crashing the application.
    await navigator.clipboard.writeText(text);
    // Side Effects: The user's clipboard content is updated with text.
    // Acceptance Criteria: When copyToClipboard is invoked with a string, that string becomes available in the system clipboard.
  } catch (error) {
    // Handle the error gracefully without crashing. Logging is appropriate for business logic.
    console.error("Failed to copy to clipboard:", error);
  }
}