import React from 'react';
import { GameStatus } from './domain';

const ScoreBoardComponent = ({ moves, timerSeconds, gameStatus, onResetGame }) => {
  const handleResetClick = () => {
    onResetGame();
  };

  const formatTime = (totalSeconds) => {
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
  };

  const formattedTime = formatTime(timerSeconds);

  return (
    <div className="flex flex-col sm:flex-row justify-between items-center p-4 bg-gray-800 text-white rounded-lg shadow-md">
      <div className="flex items-center space-x-4 mb-2 sm:mb-0">
        <div className="text-lg font-bold">Moves: {moves}</div>
        <div className="text-lg font-bold">Time: {formattedTime}</div>
      </div>
      <div className="flex items-center space-x-4">
        {gameStatus === GameStatus.Won && (
          <div className="text-xl font-extrabold text-green-400 animate-pulse">You Won!</div>
        )}
        <button
          onClick={handleResetClick}
          className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-md shadow-sm transition-colors duration-200 ease-in-out"
        >
          Reset Game
        </button>
      </div>
    </div>
  );
};

export default ScoreBoardComponent;